        import uvicorn
        uvicorn.run(self._app, host=self.host, port=self.port, workers=self.workers)

    @self._app.get("/model_info", response_model=ServeModelInfoResult)
    def model_info(self) -> ServeModelInfoResult:
        return ServeModelInfoResult(
            model_name=self._pipeline.model_name,
            model_version=self._pipeline.model_version,
            model_description=self._pipeline.model_description
        )

    @self._app.post("/tokenize", response_model=ServeTokenizeResult)
    def tokenize(self, text_input: str = Body(None, embed=True), return_ids: bool = Body(False, embed=True)) -> ServeTokenizeResult:
        tokens, token_ids = self._pipeline.tokenize(text_input)
        return ServeTokenizeResult(tokens=tokens, token_ids=token_ids) if return_ids else ServeTokenizeResult(tokens=tokens)

    @self._app.post("/detokenize", response_model=ServeDeTokenizeResult)
    def detokenize(self, tokens_ids: List[int] = Body(None, embed=True), skip_special_tokens: bool = Body(False, embed=True), cleanup_tokenization_spaces: bool = Body(True, embed=True)) -> ServeDeTokenizeResult:
        text = self._pipeline.detokenize(tokens_ids, skip_special_tokens, cleanup_tokenization_spaces)
        return ServeDeTokenizeResult(text=text)

    @self._app.post("/forward", response_model=ServeForwardResult)
    def forward(self, inputs=Body(None, embed=True)) -> ServeForwardResult:
        output = self._pipeline.forward(inputs)
        return ServeForwardResult(output=output)
