    params: Dict[str, Any]

class ActionServer:
    def __init__(self, action_dispatcher: Any) -> None:
        self._app = FastAPI()
        self._action_dispatcher = action_dispatcher

        @self._app.post("/run_action")
        async def run_action(body: RequestBody) -> Dict[str, str]:
            try:
                result, status = self._action_dispatcher.dispatch(body.action, body.params)
                return {"result": result, "status": status}
            except Exception as e:
                raise HTTPException(status_code=500, detail=str(e))

        @self._app.get("/actions_list")
        async def get_actions_list() -> Any:
            return self._action_dispatcher.get_actions_list()

    def get_app(self) -> FastAPI:
        return self._app
