import numpy as np
import os

class InferenceServer:
    def __init__(self, models_dirs: list[str] = None):
        self.app = Flask(__name__)
        self.api = Api(self.app, version='1.0', title='Inference Server API',
                       description='Serves machine learning models for inference')
        self.inferencers = {}
        if models_dirs:
            self._load_models(models_dirs)
        self._setup_routes()

    def _load_models(self, models_dirs: list[str]) -> None:
        for model_dir in models_dirs:
            for model_id in os.listdir(model_dir):
                model_path = os.path.join(model_dir, model_id)
                if os.path.isdir(model_path):
                    self.inferencers[model_id] = load_model(model_path)

    def _setup_routes(self) -> None:
        ns = self.api.namespace('models', description='Model management')
        model_list = ns.model('ModelList', {'models': fields.List(fields.String)})
        @ns.route('/')
        class ModelListEndpoint(Resource):
            @ns.doc('list_models')
            @ns.marshal_with(model_list)
            def get(self):
                return [{'id': model_id, 'metadata': inferencer.metadata} for model_id, inferencer in self.inferencers.items()]

        ns = self.api.namespace('inference', description='Model inference')
        inference_request = ns.model('InferenceRequest', {'model_id': fields.String, 'data': fields.List(fields.Float)})
        inference_response = ns.model('InferenceResponse', {'result': fields.List(fields.Float)})
        @ns.route('/')
        class InferenceEndpoint(Resource):
            @ns.doc('perform_inference')
            @ns.expect(inference_request)
            @ns.marshal_with(inference_response)
            def post(self):
                data = request.json
                model_id = data['model_id']
                if model_id not in self.inferencers:
                    return {'error': 'Model not found'}, 404
                inferencer = self.inferencers[model_id]
                result = inferencer.predict(data['data'])
                return {'result': result.tolist()}

    def run(self, host: str = '0.0.0.0', port: int = 5000) -> None:
        self.app.run(host=host, port=port)

def resp_json(data: Any, code: int = 200, headers: dict = None) -> Flask.Response:
    return jsonify(data), code, headers

class NumpyEncoder(json.JSONEncoder):
    def default(self, obj):
        if isinstance(obj, np.ndarray):
            return obj.tolist()
        elif isinstance(obj, np.float32):
            return float(obj)
        return json.JSONEncoder.default(self, obj)
