from typing import Optional
from urllib.parse import urlparse
from http.client import HTTPConnection, HTTPResponse

class Timeout:
    def __init__(self, total=None, connect=None, read=None):
        self.total = total
        self.connect = connect
        self.read = read

class Retry:
    def __init__(self, total=3, connect=None, read=None, redirect=None, status_forcelist=None):
        self.total = total
        self.connect = connect
        self.read = read
        self.redirect = redirect
        self.status_forcelist = status_forcelist

class HTTPConnectionPool:
    def __init__(self, scheme="http", ConnectionCls=HTTPConnection, host, port=None, timeout=Timeout(), retries=Retry(), proxy=None, proxy_headers=None, conn_kw=None):
        self.scheme = scheme
        self.ConnectionCls = ConnectionCls
        self.host = host
        self.port = port
        self.timeout = timeout
        self.retries = retries
        self.proxy = proxy
        self.proxy_headers = proxy_headers or {}
        self.conn_kw = conn_kw or {}
        self.num_connections = 0

    def _new_conn(self) -> HTTPConnection:
        self.num_connections += 1
        conn = self.ConnectionCls(self.host, self.port, timeout=self.timeout, **self.conn_kw)
        return conn

    def _make_request(self, conn: HTTPConnection, method: str, url: str, timeout: Timeout = None, chunked: bool = False, **httplib_request_kw) -> HTTPResponse:
        if timeout is None:
            timeout = self.timeout
        conn.request(method, url, **httplib_request_kw)
        return conn.getresponse()

    def urlopen(self, method: str, url: str, body: Optional[bytes] = None, headers: Optional[dict] = None, retries: Optional[Retry] = None, redirect: bool = True, assert_same_host: bool = True, timeout: Timeout = None, pool_timeout: Optional[float] = None, release_conn: Optional[bool] = None, chunked: bool = False, **response_kw) -> HTTPResponse:
        if timeout is None:
            timeout = self.timeout
        if retries is None:
            retries = self.retries
        if pool_timeout is None:
            pool_timeout = timeout.total
        if release_conn is None:
            release_conn = True

        conn = self._new_conn()
        try:
            resp = self._make_request(conn, method, url, timeout=timeout, chunked=chunked, **response_kw)
            if resp.status in (301, 302, 303, 307, 308) and redirect:
                location = resp.getheader("Location")
                parsed_location = urlparse(location)
                if parsed_location.netloc:
                    self.host = parsed_location.netloc
                    self.port = parsed_location.port
                url = parsed_location.path or "/"
                return self.urlopen(method, url, body=body, headers=headers, retries=retries, redirect=redirect, assert_same_host=assert_same_host, timeout=timeout, pool_timeout=pool_timeout, release_conn=release_conn, chunked=chunked, **response_kw)
            return resp
        finally:
            if release_conn:
                conn.close()
