        self.login_manager.login_view = 'login'
        self.login_manager.login_message_category = 'info'
        self.User = self.create_user_model()
        self.SettingForm = self.create_setting_form()
        self.LoginForm = self.create_login_form()
        self.RegistrationForm = self.create_registration_form()
        self.CaseForm = self.create_case_form()
        self.create_routes()

    def run(self, host='0.0.0.0', port=8080):
        self.app.run(host=host, port=port)

    def login_required(self, role="ANY"):
        def decorator(func):
            @wraps(func)
            def wrapper(*args, **kwargs):
                if not current_user.is_authenticated:
                    return redirect(url_for('login'))
                if role != "ANY" and current_user.role != role:
                    return redirect(url_for('unauthorized'))
                return func(*args, **kwargs)
            return wrapper
        return decorator

    @login_manager.user_loader
    def load_user(user_id):
