from flask import Flask, Response, request
from flask.json import jsonify
from functools import wraps

class FlaskApp(Flask):
    def __init__(self, *args, **kwargs):
        super().__init__(*args, **kwargs)
        self.request_class = request
        self.json_data_class = (dict, list, tuple)

    def make_response(self, rv):
        if isinstance(rv, self.json_data_class):
            return Response(jsonify(rv), mimetype='application/json')
        return super().make_response(rv)

    def get(self, rule, **options):
        def decorator(f):
            @wraps(f)
            def decorated_function(*args, **kwargs):
                return f(*args, **kwargs)
            return self.route(rule, methods=['GET'], **options)(decorated_function)
        return decorator

    def post(self, rule, **options):
        def decorator(f):
            @wraps(f)
            def decorated_function(*args, **kwargs):
                return f(*args, **kwargs)
            return self.route(rule, methods=['POST'], **options)(decorated_function)
        return decorator
