from werkzeug.debug import DebuggedApplication

class FlaskAPI:
    def __init__(self):
        self.app = Flask(__name__)
        self._setup_routes()
        self.server_thread = None

    def _setup_routes(self):
        @self.app.route('/api/status', methods=['GET'])
        def status():
            return jsonify({'status': 'OK'})

        @self.app.route('/api/process', methods=['POST'])
        def process():
            data = request.json
            text = data.get('text', '')
            screen = data.get('screen', '')
            talk = data.get('talk', '')
            return jsonify(self._the_input(text, screen, talk))

        @self.app.route('/api/stop', methods=['POST'])
        def stop():
            self.stop_api()
            return jsonify({'status': 'Server stopped'})

    def _the_input(self, text: str, screen: str, talk: str) -> dict:
        # Process input text and optional screenshot, return response
        return {'response': 'Processed: ' + text}

    def start_api(self, api: bool = False):
        if api:
            serve(self.app, host='0.0.0.0', port=5000)
        else:
            self.server_thread = ServerThread(self.app, '0.0.0.0', 5000)
            self.server_thread.start()

    def stop_api(self):
        if self.server_thread:
            self.server_thread.shutdown()
            self.server_thread.join()

class ServerThread(Thread):
    def __init__(self, app: Flask, host: str, port: int):
        super().__init__()
        self.srv = WSGIServer((host, port), DispatcherMiddleware(DebuggedApplication(app), {'/__debug__': DebuggedApplication(app)}))
        self.ctx = app.app_context()
        self.ctx.push()

    def run(self):
        self.srv.serve_forever()

    def shutdown(self):
        self.srv.shutdown()
