from onnxModifier import onnxModifier

class ONNXModifierServer:
    def __init__(self):
        self.app = Flask(__name__)
        self._setup_routes()
        self.onnx_modifier = None

    def _setup_routes(self):
        @self.app.route('/')
        def index():
            return render_template('index.html')

        @self.app.route('/open_model', methods=['POST'])
        def open_model():
            if 'file' not in request.files:
                return jsonify({'error': 'No file part'}), 400
            file = request.files['file']
            if file.filename == '':
                return jsonify({'error': 'No selected file'}), 400
            if file:
                self.onnx_modifier = onnxModifier(file)
                return jsonify({'message': 'Model opened successfully'}), 200

        @self.app.route('/modify_and_download_model', methods=['POST'])
        def modify_and_download_model():
            if not self.onnx_modifier:
                return jsonify({'error': 'No model opened'}), 400
            modifications = request.get_json()
            if not modifications:
                return jsonify({'error': 'No modifications provided'}), 400
            self.onnx_modifier.apply_modifications(modifications)
            save_path = self.onnx_modifier.save_model()
            return send_from_directory(self.onnx_modifier.model_dir, save_path, as_attachment=True)

    def run(self, host='127.0.0.1', port=5000, debug=False):
        self.app.run(host=host, port=port, debug=debug)
