import typing
from urllib.parse import Url
from http.client import HTTPConnection, HTTPResponse, _TYPE_TIMEOUT, _DEFAULT_TIMEOUT, _TYPE_BODY, _TYPE_SOCKET_OPTIONS, _ResponseOptions, connection

class HTTPConnection(HTTPConnection):
    default_port: typing.ClassVar[int] = 80
    default_socket_options: typing.ClassVar[connection._TYPE_SOCKET_OPTIONS] = (connection.TCP_NODELAY,)
    socket_options: Optional[connection._TYPE_SOCKET_OPTIONS] = None
    proxy: Optional[Url] = None
    proxy_config: Optional[ProxyConfig] = None
    _has_connected_to_proxy: bool = False
    _response_options: Optional[_ResponseOptions] = None
    _tunnel_host: Optional[str] = None
    _tunnel_port: Optional[int] = None
    _tunnel_scheme: Optional[str] = None

    def __init__(self, host: str, port: Optional[int] = None, *, timeout: _TYPE_TIMEOUT = _DEFAULT_TIMEOUT, source_address: Optional[tuple[str, int]] = None, blocksize: int = 16384, socket_options: Optional[connection._TYPE_SOCKET_OPTIONS] = default_socket_options, proxy: Optional[Url] = None, proxy_config: Optional[ProxyConfig] = None) -> None:
        super().__init__(host, port, timeout=timeout, source_address=source_address, blocksize=blocksize, socket_options=socket_options)
        self.proxy = proxy
        self.proxy_config = proxy_config

    def _new_conn(self) -> socket.socket:
        try:
            sock = super()._new_conn()
            if self.socket_options:
                sock.setsockopt(*self.socket_options)
            return sock
        except Exception as e:
            raise ConnectionError(f"Failed to create a new connection: {e}")

    def connect(self) -> None:
        if self.proxy:
            self._connect_to_proxy()
        else:
            super().connect()

    def _connect_to_proxy(self) -> None:
        if not self.proxy:
            raise ValueError("Proxy URL is not set")
        if not self.proxy_config:
            raise ValueError("Proxy configuration is not set")
        self.sock = self._new_conn()
        self.sock.connect((self.proxy.host, self.proxy.port))
        self._has_connected_to_proxy = True

    def request(self, method: str, url: str, body: Optional[_TYPE_BODY] = None, headers: Optional[Mapping[str, str]] = None, *, chunked: bool = False, preload_content: bool = True, decode_content: bool = True, enforce_content_length: bool = True) -> None:
        if self._has_connected_to_proxy:
            if not self._tunnel_host:
                raise ValueError("Tunnel host is not set")
            if not self._tunnel_port:
                raise ValueError("Tunnel port is not set")
            if not self._tunnel_scheme:
                raise ValueError("Tunnel scheme is not set")
            self._tunnel()
        super().request(method, url, body, headers, chunked=chunked, preload_content=preload_content, decode_content=decode_content, enforce_content_length=enforce_content_length)

    def _tunnel(self) -> None:
        self.putrequest("CONNECT", f"{self._tunnel_host}:{self._tunnel_port}", skip_host=True, skip_accept_encoding=True)
        self.endheaders()
        response = self.getresponse()
        if response.status != 200:
            raise ConnectionError(f"Failed to establish a tunnel: {response.status} {response.reason}")
        response.read()

    def close(self) -> None:
        if self._has_connected_to_proxy:
            self._close_proxy_connection()
        super().close()

    def _close_proxy_connection(self) -> None:
        if not self.proxy:
            raise ValueError("Proxy URL is not set")
        self.sock.close()
        self.sock = None
        self._has_connected_to_proxy = False
