        try:
            self._conn.send(data)
        except Exception as e:
            if self._tries < self.maxtries:
                self._tries += 1
                time.sleep(self._delay)
                self._delay *= 2
                self._reconnect()
            else:
                raise e

    def close(self):
        if self._closed:
            return self._bytes
        self._closed = True
        if self._isconnected():
            self._conn.close()
        return self._getresponse()

