            self._conn = http.client.HTTPSConnection(self._server, self._port, context=self._get_ssl_context())
        else:
            self._conn = http.client.HTTPConnection(self._server, self._port)
        self._conn.request("POST", self._url, headers=self._headers)

    def _getresponse(self):
        if self._isconnected():
            response = self._conn.getresponse()
            if response.status in (200, 502):
                self._bytes += response.read()
            self._conn.close()
        return self._bytes

    def _isconnected(self):
        return self._conn is not None and not self._conn.sock._closed

    def _reconnect(self):
        self._reset_retries()
        self._conn.close()
        self._connect()

    def _reset_retries(self):
        self._tries = 0
        self._delay = 1

    def _get_ssl_context(self):
        if self._ssl_enabled:
            context = ssl.create_default_context()
            if not self._ssl_verification_enabled:
                context.check_hostname = False
                context.verify_mode = ssl.CERT_NONE
            return context

    def _get_proxy_config(self):
        proxy_host = os.getenv("HTTP_PROXY_HOST")
        proxy_port = os.getenv("HTTP_PROXY_PORT")
