sessions = []

class DNSTwistAPI:
    def __init__(self):
        self.app = app
        self._setup_routes()
        self._start_janitor()

    def _setup_routes(self):
        self.app.add_url_rule('/', 'root', self.root)
        self.app.add_url_rule('/api/scan', 'api_scan', self.api_scan, methods=['POST'])
        self.app.add_url_rule('/api/status/<sid>', 'api_status', self.api_status)
        self.app.add_url_rule('/api/domains/<sid>', 'api_domains', self.api_domains)
        self.app.add_url_rule('/api/csv/<sid>', 'api_csv', self.api_csv)
        self.app.add_url_rule('/api/json/<sid>', 'api_json', self.api_json)
        self.app.add_url_rule('/api/list/<sid>', 'api_list', self.api_list)
        self.app.add_url_rule('/api/stop/<sid>', 'api_stop', self.api_stop)

    def _start_janitor(self):
        Thread(target=self.janitor, daemon=True).start()

    def janitor(self):
        while True:
            for session in sessions[:]:
                if session['status'] == 'completed' or session['expires'] < datetime.now():
                    sessions.remove(session)
            sleep(60)

    @auth.login_required
    def root(self):
        return self.app.send_static_file('index.html')

    @auth.login_required
    def api_scan(self):
        sid = str(os.urandom(16).hex())
        domain = request.json.get('domain')
        if not domain:
            return jsonify({'error': 'No domain provided'}), 400
        session = {'sid': sid, 'domain': domain, 'status': 'running', 'expires': datetime.now() + timedelta(hours=1), 'results': []}
        sessions.append(session)
        Thread(target=self._scan, args=(session,), daemon=True).start()
        return jsonify({'sid': sid}), 202

    @auth.login_required
    def api_status(self, sid):
        session = next((s for s in sessions if s['sid'] == sid), None)
        if not session:
            return jsonify({'error': 'Session not found'}), 404
        return jsonify({'status': session['status'], 'expires': session['expires'].isoformat()}), 200

    @auth.login_required
    def api_domains(self, sid):
        session = next((s for s in sessions if s['sid'] == sid), None)
        if not session:
            return jsonify({'error': 'Session not found'}), 404
        return jsonify({'domains': session['results']}), 200

    @auth.login_required
    def api_csv(self, sid):
        session = next((s for s in sessions if s['sid'] == sid), None)
        if not session:
            return jsonify({'error': 'Session not found'}), 404
        if session['status'] != 'completed':
            return jsonify({'error': 'Session not completed'}), 422
        output = io.StringIO()
        writer = csv.writer(output)
        writer.writerow(['Domain', 'Registered'])
        for domain in session['results']:
            writer.writerow([domain, 'Yes' if session['results'][domain] else 'No'])
        output.seek(0)
        return Response(output, mimetype='text/csv'), 200

    @auth.login_required
    def api_json(self, sid):
        session = next((s for s in sessions if s['sid'] == sid), None)
        if not session:
            return jsonify({'error': 'Session not found'}), 404
        if session['status'] != 'completed':
            return jsonify({'error': 'Session not completed'}), 422
        return jsonify(session['results']), 200

    @auth.login_required
    def api_list(self, sid):
        session = next((s for s in sessions if s['sid'] == sid), None)
        if not session:
            return jsonify({'error': 'Session not found'}), 404
        if session['status'] != 'completed':
            return jsonify({'error': 'Session not completed'}), 422
        return Response('\n'.join(session['results']), mimetype='text/plain'), 200

    @auth.login_required
    def api_stop(self, sid):
        session = next((s for s in sessions if s['sid'] == sid), None)
        if not session:
            return jsonify({'error': 'Session not found'}), 404
        session['status'] = 'stopped'
        return jsonify({'status': 'stopped'}), 200

    def _scan(self, session):
        domain = session['domain']
        results = dnstwist.dnstwist(domain)
        session['results'] = {r: True for r in results}
        session['status'] = 'completed'

    def run(self, host='0.0.0.0', port=5000):
        self.app.run(host=host, port=port)
