from flask import Flask, request, render_template
import pickle
import sqlite3

class ReviewClassifier:
    def __init__(self):
        self.app = Flask(__name__)
        self.clf = pickle.load(open('classifier.pkl', 'rb'))
        self.db = 'reviews.db'
        self._setup_routes()

    def _setup_routes(self):
        self.app.add_url_rule('/', 'index', self.index, methods=['GET', 'POST'])
        self.app.add_url_rule('/results', 'results', self.results, methods=['POST'])
        self.app.add_url_rule('/feedback', 'feedback', self.feedback, methods=['POST'])

    def classify(self, document):
        label, prob = self.clf.predict(document)
        return label, prob

    def train(self, document, y):
        self.clf.partial_fit(document, y)

    def sqlite_entry(self, document, y):
        conn = sqlite3.connect(self.db)
        c = conn.cursor()
        c.execute("INSERT INTO reviews (document, sentiment) VALUES (?, ?)", (document, y))
        conn.commit()
        conn.close()

    def index(self):
        return render_template('index.html')

    def results(self):
        document = request.form['document']
        label, prob = self.classify(document)
        return render_template('results.html', label=label, prob=prob)

    def feedback(self):
        document = request.form['document']
        y = int(request.form['y'])
        self.train(document, y)
        self.sqlite_entry(document, y)
        return render_template('feedback.html')

    def run(self, debug=True):
        self.app.run(debug=debug)
