from sanic import Sanic
from sanic.response import HTTPResponse, json
from collections import defaultdict
from typing import Union, List, Optional
from .inference_engine import InferenceEngineWrapper
from .twe import TopicalWordEmbeddingsWrapper

class FamiliaAPI:
    def __init__(self, model_path: str, model_name: str, n_workers: int = None):
        self.app = Sanic("FamiliaAPI")
        self.model_name = model_name
        self.model_path = model_path
        self.n_workers = n_workers
        self.model_dir = f"{model_path}/{model_name}"
        self.emb_file = f"{self.model_dir}/word_vectors.txt"
        self.inference_engine_lda = InferenceEngineWrapper(self.model_dir, 'lda', n_workers)
        self.inference_engine_slda = InferenceEngineWrapper(self.model_dir, 'slda', n_workers)
        self.twe = TopicalWordEmbeddingsWrapper(self.emb_file)
        self.lda_topic_words = self._read_topic_words_from_file()
        self._setup_routes()

    def _read_topic_words_from_file(self, topic_words_file_name: str = 'topic_words.lda.txt') -> defaultdict:
        topic_words = defaultdict(list)
        with open(f"{self.model_dir}/{topic_words_file_name}", 'r') as f:
            for line in f:
                topic, words = line.strip().split('\t')
                topic_words[topic] = words.split(',')
        return topic_words

    def _get_param(self, request, param_name, default_value=None, is_list=False):
        param = request.args.get(param_name)
        if param is None:
            return default_value
        if is_list:
            return param.split(',')
        return param

    def _strip_to_none(self, text: str) -> Optional[str]:
        text = text.strip()
        if not text:
            return None
        return text

    def _response(self, success: bool = True, data=None, message=None) -> HTTPResponse:
        return json({'success': success, 'data': data, 'message': message})

    def _error_response(self, message: str = 'Invalid request') -> HTTPResponse:
        return self._response(success=False, message=message)

    def _setup_routes(self):
        @self.app.route('/tokenize', methods=['POST'])
        async def tokenize(request):
            text = self._strip_to_none(request.json.get('text'))
            if text is None:
                return self._error_response('No text provided')
            tokens = self.twe.tokenize(text)
            return self._response(data={'tokens': tokens})

        @self.app.route('/lda', methods=['POST'])
        async def lda(request):
            text = self._strip_to_none(request.json.get('text'))
            if text is None:
                return self._error_response('No text provided')
            tokens = self.twe.tokenize(text)
            topics = self.inference_engine_lda.infer(tokens)
            return self._response(data={'topics': topics})

        @self.app.route('/slda', methods=['POST'])
        async def slda(request):
            text = self._strip_to_none(request.json.get('text'))
            if text is None:
                return self._error_response('No text provided')
            tokens = self.twe.tokenize(text)
            topics = self.inference_engine_slda.infer(tokens)
            return self._response(data={'topics': topics})

    def run(self):
        self.app.run(host='0.0.0.0', port=8000, workers=self.n_workers)
