class S(BaseHTTPRequestHandler):
    def _set_headers(self):
        self.send_response(200)
        self.send_header('Content-type', 'text/html')
        self.end_headers()

    def write(self, text):
        self.wfile.write(bytes(text, 'utf8'))

    def do_GET(self):
        self._set_headers()
        sentence = self.path[1:]
        self.write(f'<html><body><p>{sentence}</p></body></html>')

    def do_HEAD(self):
        self._set_headers()

    def do_POST(self):
        self._set_headers()
        self.write('POST!')


class HTTPServerWrapper:
    def __init__(self, port=8765, server_class=HTTPServer, handler_class=S):
