import threading
import tornado.ioloop
import tornado.web

class JSONEchoServer(threading.Thread):
    def __init__(self, lock, port, *args, **kw):
        super().__init__(*args, **kw)
        self.lock = lock
        self.port = port
        self._stop = threading.Event()
        self.daemon = True

    def stop(self):
        self._stop.set()

    def stopped(self):
        return self._stop.is_set()

    def setup_application(self):
        class JSONHandler(tornado.web.RequestHandler):
            def get(self):
                self.write({"method": "GET", "url": self.request.uri})

            def post(self):
                self.write({"method": "POST", "url": self.request.uri, "body": self.request.body})

        return tornado.web.Application([
            (r"/.*", JSONHandler),
        ])

    def run(self):
        self.lock.acquire()
        app = self.setup_application()
        app.listen(self.port)
        tornado.ioloop.IOLoop.current().start()
        self.lock.release()
