class JSONRPCExecutor:
    def __init__(self, command: Union[str, List[str]], url: str, jsonrpc_method: str, jsonrpc_params: Optional[List[Any]] = None, status: str = "^2\d\d$", result_validator: Optional[Callable[[Any], Tuple[bool, Optional[str]]]] = None, verify_tls: bool = True, method: str = "POST"):
        self.command = command
        self.url = url
        self.jsonrpc_method = jsonrpc_method
        self.jsonrpc_params = jsonrpc_params if jsonrpc_params is not None else []
        self.status = status
        self.result_validator = result_validator
        self.verify_tls = verify_tls
        self.method = method

    def _send_request(self, conn: Union[HTTPConnection, HTTPSConnection]) -> None:
        headers = {"Content-Type": "application/json"}
        payload = {
            "jsonrpc": "2.0",
            "method": self.jsonrpc_method,
            "params": self.jsonrpc_params,
            "id": 1
        }
        conn.request(self.method, self.url, json.dumps(payload), headers)
        response = conn.getresponse()
        if not response.status or not re.match(self.status, str(response.status)):
            raise Exception(f"Unexpected status code: {response.status}")
        data = response.read()
        if self.result_validator:
            valid, message = self.result_validator(json.loads(data))
            if not valid:
                raise Exception(message)
        return json.loads(data)

    def _validate_response(self, response) -> bool:
        return True

    def execute(self) -> bool:
        conn = HTTPSConnection(self.url) if "https" in self.url else HTTPConnection(self.url)
        try:
            if isinstance(self.command, list):
                for cmd in self.command:
                    conn.request(self.method, self.url, cmd, {"Content-Type": "application/json"})
                    response = conn.getresponse()
                    if not response.status or not re.match(self.status, str(response.status)):
                        raise Exception(f"Unexpected status code: {response.status}")
            else:
                self._send_request(conn)
            return True
        finally:
            conn.close()
