from abc import ABC, abstractmethod
from typing import Any, AsyncCallable, Dict, Iterable, List, Optional, Sequence, Tuple
from aiokafka import AIOKafkaConsumer, TopicPartition
from aiokafka.errors import KafkaError


class LogicSubscriber(ABC):
    def __init__(
        self,
        *topics: str,
        group_id: Optional[str] = None,
        connection_args: Dict[str, Any] = {},
        partitions: Iterable[TopicPartition] = (),
        default_parser: AsyncCallable = None,
        default_decoder: AsyncCallable = None,
    ) -> None:
        self.topics = topics
        self.group_id = group_id
        self.consumer = None
        self.partitions = partitions
        self.__connection_args = connection_args
        self.__default_parser = default_parser
        self.__default_decoder = default_decoder

    async def start(self) -> None:
        self.consumer = AIOKafkaConsumer(
            *self.topics,
            group_id=self.group_id,
            loop=self.__connection_args.get("loop", None),
            **self.__connection_args,
        )
        if self.partitions:
            await self.consumer.assign(self.partitions)
        else:
            await self.consumer.subscribe(self.topics)
        await self.consumer.start()

    async def close(self) -> None:
        if self.consumer:
            await self.consumer.stop()
            await self.consumer.close()

    @abstractmethod
    async def get_msg(self) -> Any:
        pass

    async def _consume(self) -> None:
        try:
            async for msg in self.consumer:
                if msg.error:
                    if msg.error.code() != KafkaError._PARTITION_EOF:
                        raise msg.error
                else:
                    await self.get_msg(msg)
        except Exception as e:
            print(f"Error consuming messages: {e}")
        finally:
            await self.close()


class DefaultSubscriber(LogicSubscriber):
    async def get_msg(self, msg: Any) -> Any:
        if self.__default_parser:
            msg = await self.__default_parser(msg)
        if self.__default_decoder:
            msg = await self.__default_decoder(msg)
        return msg


class BatchSubscriber(LogicSubscriber):
    def __init__(
        self,
        *topics: str,
        batch_timeout_ms: int,
        max_records: Optional[int] = None,
        **kwargs: Any,
    ) -> None:
        super().__init__(*topics, **kwargs)
        self.batch_timeout_ms = batch_timeout_ms
        self.max_records = max_records

    async def get_msg(self) -> Tuple[Any, ...]:
        batch = []
        try:
            async for msg in self.consumer:
                if msg.error:
                    if msg.error.code() != KafkaError._PARTITION_EOF:
                        raise msg.error
                else:
                    batch.append(msg)
                    if self.max_records and len(batch) >= self.max_records:
                        break
            if self.__default_parser:
                batch = await self.__default_parser(batch)
            if self.__default_decoder:
                batch = await self.__default_decoder(batch)
            return tuple(batch)
        except Exception as e:
            print(f"Error consuming messages: {e}")
        finally:
            await self.close()
