        self.__url = urllib.parse.urlparse(service_url)
        self.__id_count = 0
        self.__auth_header = None
        if btc_conf_file:
            with open(btc_conf_file) as f:
                btc_conf = json.load(f)
            self.__auth_header = base64.b64encode(
                ('%s:%s' % (btc_conf['rpcuser'], btc_conf['rpcpassword'])).encode('utf-8')
            ).strip()
        self.__conn = httplib.HTTPConnection(self.__url.hostname, self.__url.port, timeout=timeout)
        if self.__url.scheme == 'https':
            self.__conn = httplib.HTTPSConnection(self.__url.hostname, self.__url.port, timeout=timeout, context=ssl._create_unverified_context())

    def _call(self, service_name, *args):
        self.__id_count += 1
        request = {
            'jsonrpc': '2.0',
            'method': service_name,
            'params': args,
            'id': self.__id_count
        }
        headers = {
            'Content-Type': 'application/json',
            'Accept': 'application/json'
        }
        if self.__auth_header:
            headers['Authorization'] = 'Basic %s' % self.__auth_header.decode('utf-8')
        self.__conn.request('POST', self.__url.path, json.dumps(request), headers)
        response = self.__get_response()
        if 'error' in response:
            raise Exception(response['error']['message'])
        return response['result']

    def _get_response(self):
        response = self.__conn.getresponse()
        response_data = response.read().decode('utf-8')
        return json.loads(response_data)

    def close(self):
        self.__conn.close()

    def __del__(self):
        self.close()
