class ffmpeg:
    class Error(Exception):
        def __init__(self, message: str, returncode: int, stderr: str):
            super().__init__(message)
            self.returncode = returncode
            self.stderr = stderr

    @staticmethod
    def _probe(filename: str, cmd: str = "ffprobe", timeout: Optional[int] = None, **kwargs: Dict) -> Dict:
        cmd = [cmd, "-v", "quiet", "-print_format", "json", "-show_format", "-show_streams", filename]
        cmd.extend(kwargs.get("args", []))
        try:
            process = subprocess.Popen(cmd, stdout=PIPE, stderr=PIPE)
            stdout, stderr = process.communicate(timeout=timeout)
        except subprocess.TimeoutExpired:
            process.kill()
            stdout, stderr = process.communicate()
            raise ffmpeg.Error("ffprobe command timed out", process.returncode, stderr.decode())
        except Exception as e:
            raise ffmpeg.Error("ffprobe command failed", -1, str(e))
        if process.returncode != 0:
            raise ffmpeg.Error("ffprobe command failed", process.returncode, stderr.decode())
        return json.loads(stdout.decode())
