import socket
import struct
import random
import string
import base64
import re

class socksocket(socket.socket):
    default_proxy = ('socks5', '127.0.0.1', 1080, False, None, None)
    _proxyconn = None
    proxy = None
    proxy_sockname = None
    proxy_peername = None
    _timeout = None

    def __init__(self, family=socket.AF_INET, type=socket.SOCK_STREAM, proto=0, *args, **kwargs):
        super(socksocket, self).__init__(family, type, proto, *args, **kwargs)
        if type != socket.SOCK_STREAM:
            raise ValueError('Only SOCK_STREAM sockets are supported')
        self._timeout = self.gettimeout()

    def set_proxy(self, proxy_type, addr, port, rdns=True, username=None, password=None):
        if proxy_type not in ('socks4', 'socks5', 'http'):
            raise ValueError('Unsupported proxy type')
        self.proxy = (proxy_type, addr, port, rdns, username, password)

    def connect(self, dest_pair):
        if self.proxy is None:
            return super(socksocket, self).connect(dest_pair)
        if self.proxy[0] == 'socks4':
            self._negotiate_SOCKS4(dest_pair[0], dest_pair[1])
        elif self.proxy[0] == 'socks5':
            self._negotiate_SOCKS5(dest_pair[0], dest_pair[1])
        elif self.proxy[0] == 'http':
            self._negotiate_HTTP(dest_pair[0], dest_pair[1])
        self.proxy_peername = dest_pair

    def _readall(self, file, count):
        data = b''
        while len(data) < count:
            chunk = file.recv(count - len(data))
            if not chunk:
                raise EOFError('Connection closed unexpectedly')
            data += chunk
        return data

    def _proxy_addr(self):
        if self.proxy[3]:
            return socket.gethostbyname(self.proxy[1]), self.proxy[2]
        return self.proxy[1], self.proxy[2]

    def _negotiate_SOCKS4(self, dest_addr, dest_port):
        proxy_addr = self._proxy_addr()
        self.connect(proxy_addr)
        self.send(struct.pack('>BBH', 4, 1, dest_port) + socket.inet_aton(dest_addr))
        resp = self._readall(self, 8)
        if resp[0] != 0:
            raise Exception('Server returned error: ' + repr(resp))

    def _negotiate_SOCKS5(self, dest_addr, dest_port):
        proxy_addr = self._proxy_addr()
        self.connect(proxy_addr)
        self.send(b'\x05\x01\x00')
        resp = self._readall(self, 2)
        if resp[0] != 0x05 or resp[1] != 0x00:
            raise Exception('Server returned error: ' + repr(resp))
        if dest_addr.startswith('['):
            self.send(b'\x05\x03\x03' + len(dest_addr) + dest_addr.encode('utf-8') + struct.pack('>H', dest_port))
        else:
            self.send(b'\x05\x01\x00' + socket.inet_aton(dest_addr) + struct.pack('>H', dest_port))
        resp = self._readall(self, 10)
        if resp[0] != 0x05 or resp[1] != 0x00 or resp[3] not in (0x01, 0x03):
            raise Exception('Server returned error: ' + repr(resp))
        if resp[3] == 0x03:
            dest_addr = self._readall(self, resp[4])
        if resp[1] == 0x01:
            dest_addr = socket.inet_ntoa(resp[4:8])
        if resp[1] == 0x03:
            dest_addr = self._readall(self, resp[4])
        dest_port = struct.unpack('>H', resp[-2:])[0]
        self.proxy_peername = (dest_addr, dest_port)

    def _negotiate_HTTP(self, dest_addr, dest_port):
        proxy_addr = self._proxy_addr()
        self.connect(proxy_addr)
        auth = ''
        if self.proxy[4] is not None:
            auth = 'Proxy-Authorization: Basic ' + base64.b64encode((self.proxy[4] + ':' + self.proxy[5]).encode('utf-8')).decode('utf-8') + '\r\n'
        self.send('CONNECT ' + dest_addr + ':' + str(dest_port) + ' HTTP/1.1\r\n' + auth + 'Host: ' + dest_addr + '\r\n\r\n')
        resp = self._readall(self.makefile('r'), 4096)
        if re.search('HTTP/(\d\.\d) (\d{3})', resp).group(2) != '200':
            raise Exception('Server returned error: ' + resp)
