import socket
import threading
import time
from typing import List, Optional, Dict, Any

from . import common


class Client:
    def __init__(self, host: str = common.DEFAULT_HOST, port: int = common.DEFAULT_PORT) -> None:
        self.host = host
        self.port = port
        self.pending_commands: List[common.Command] = []
        self.socket: Optional[socket.socket] = None
        self.client_id: Optional[str] = None
        self.current_custom_attributes: Dict[str, Any] = {}
        self.clients_attributes: Dict[str, Dict[str, Any]] = {}
        self.rooms_attributes: Dict[str, Dict[str, Any]] = {}
        self.current_room: Optional[str] = None

    def __del__(self) -> None:
        self.disconnect()

    def connect(self) -> None:
        self.socket = socket.socket(socket.AF_INET, socket.SOCK_STREAM)
        self.socket.connect((self.host, self.port))
        self.socket.setblocking(False)
        self.send_command(common.Command("init", {}))
        threading.Thread(target=self._receive_messages, daemon=True).start()

    def disconnect(self) -> None:
        if self.socket:
            self.socket.close()

    def is_connected(self) -> bool:
        return self.socket is not None and self.socket.fileno() != -1

    def send_command(self, command: common.Command) -> bool:
        if not self.is_connected():
            return False
        self.pending_commands.append(command)
        return True

    def handle_connection_lost(self) -> None:
        self.socket = None
        self.client_id = None
        self.current_custom_attributes = {}
        self.clients_attributes = {}
        self.rooms_attributes = {}
        self.current_room = None

    def _receive_messages(self) -> None:
        while self.is_connected():
            try:
                data = self.socket.recv(4096)
                if not data:
                    raise ConnectionResetError
                messages = data.decode("utf-8").split("\n")
                for message in messages:
                    if message:
                        self._process_message(message)
            except ConnectionResetError:
                self.handle_connection_lost()
                break
            except Exception as e:
                print(f"Error receiving messages: {e}")
                break
            time.sleep(0.01)

    def _process_message(self, message: str) -> None:
        try:
            command = common.Command.from_json(message)
            if command.name == "init":
                self.client_id = command.data.get("client_id")
                self.clients_attributes = command.data.get("clients_attributes", {})
                self.rooms_attributes = command.data.get("rooms_attributes", {})
            elif command.name == "client_update":
                self.clients_attributes = command.data.get("clients_attributes", {})
            elif command.name == "room_update":
                self.rooms_attributes = command.data.get("rooms_attributes", {})
            elif command.name == "room_join":
                self.current_room = command.data.get("room_id")
            elif command.name == "room_leave":
                self.current_room = None
            elif command.name == "client_attributes_update":
                self.current_custom_attributes = command.data.get("attributes", {})
            elif command.name == "error":
                print(f"Server error: {command.data.get('message')}")
        except Exception as e:
            print(f"Error processing message: {e}")

    def fetch_incoming_commands(self) -> List[common.Command]:
        commands = []
        while self.pending_commands:
            commands.append(self.pending_commands.pop(0))
        return commands

    def fetch_outgoing_commands(self, commands_send_interval: int = 0) -> None:
        if commands_send_interval > 0:
            time.sleep(commands_send_interval)
        if self.pending_commands:
            command = self.pending_commands.pop(0)
            self.socket.send(command.to_json().encode("utf-8"))

    def fetch_commands(self, commands_send_interval: int = 0) -> List[common.Command]:
        self.fetch_outgoing_commands(commands_send_interval)
        return self.fetch_incoming_commands()
