import socket
import re

class ZookeeperTCPClient:
    def get_raw_stats(self, host, port):
        try:
            if port is None:
                sock = socket.socket(socket.AF_UNIX, socket.SOCK_STREAM)
                sock.connect(host)
            else:
                sock = socket.socket(socket.AF_INET, socket.SOCK_STREAM)
                sock.connect((host, port))
            sock.sendall(b"mntr\n")
            data = sock.recv(1024).decode()
            sock.close()
            return data
        except Exception as e:
            print(f"Error connecting to Zookeeper server: {e}")
            return None

    def get_stats(self, host, port):
        raw_stats = self.get_raw_stats(host, port)
        if raw_stats is None:
            return None
        stats = {}
        for line in raw_stats.splitlines():
            if line.startswith('#') or line.strip() == '':
                continue
            key, value = line.split('\t')
            if key not in ['zk_version', 'zk_avg_latency', 'zk_max_latency', 'zk_min_latency', 'zk_packets_received', 'zk_packets_sent', 'zk_num_alive_connections', 'zk_outstanding_requests', 'zk_server_state', 'zk_znode_count', 'zk_watch_count', 'zk_ephemerals_count', 'zk_approximate_data_size', 'zk_open_file_descriptor_count', 'zk_max_file_descriptor_count', 'zk_followers', 'zk_synced_followers', 'zk_pending_syncs']:
                stats[key] = value
        try:
            process_info = raw_stats.splitlines()[0].split('\t')
            max_connections = re.search(r'max_connections\s+(\d+)', process_info[1]).group(1)
            stats['max_connections'] = max_connections
        except:
            pass
        return stats
