import threading

class AdbConnector:
    def __init__(self, adb_host: str = 'localhost') -> None:
        self.adb_host = adb_host
        self.socket = None
        self.packet_buffer = b''
        self._disposed = False
        self.adb_proc = None
        self._relaunch_adb_bridge()

    def _relaunch_adb_bridge(self) -> None:
        if self.adb_proc:
            self.adb_proc.kill()
        self.adb_proc = subprocess.Popen(['adb', '-H', self.adb_host, 'tcpip', '5555'])
        self.socket = socket.socket(socket.AF_INET, socket.SOCK_STREAM)
        self.socket.connect((self.adb_host, 5555))

    def send_request(self, packet_type: int, packet_payload: bytes) -> None:
        packet = self._create_packet(packet_type, packet_payload)
        self.socket.sendall(packet)

    def read_loop(self) -> None:
        while not self._disposed:
            try:
                data = self.socket.recv(4096)
                if not data:
                    self._relaunch_adb_bridge()
                    continue
                self.packet_buffer += data
                while len(self.packet_buffer) >= 4:
                    packet_length = int.from_bytes(self.packet_buffer[:4], byteorder='little')
                    if len(self.packet_buffer) >= packet_length + 4:
                        packet = self.packet_buffer[4:packet_length + 4]
                        self.packet_buffer = self.packet_buffer[packet_length + 4:]
                        self._process_packet(packet)
                    else:
