import socket
import json

class TwemproxyStatsCollector:
    GAUGES = ['uptime', 'rx', 'tx', 'requests', 'responses', 'errors', 'replies', 'client_eof', 'server_eof', 'client_connections', 'server_connections', 'client_timedout', 'server_timedout', 'client_slow', 'server_slow']
    IGNORED = ['server', 'client']

    def get_raw_stats(self, host, port):
        s = socket.socket(socket.AF_INET, socket.SOCK_STREAM)
        s.connect((host, port))
        s.send(b'stats\n')
        data = s.recv(4096)
        s.close()
        return json.loads(data.decode('utf-8'))

    def get_stats(self, host, port):
        raw_stats = self.get_raw_stats(host, port)
        general_stats = {}
        pool_stats = {}
        for key, value in raw_stats.items():
            if key in self.GAUGES:
                general_stats[key] = value
            elif key.startswith('pool.'):
                pool_name = key.split('.')[1]
                if pool_name not in pool_stats:
                    pool_stats[pool_name] = {}
                pool_stats[pool_name][key] = value
        return general_stats, pool_stats

    def collect_stats(self, hosts):
        stats = {}
        for host in hosts:
            alias, host, port = host.split(':')
            general_stats, pool_stats = self.get_stats(host, int(port))
            stats[alias] = {'general': general_stats, 'pools': pool_stats}
        return stats
