            len_data = self.buffer[:4]
        else:
            len_data = self.s.recv(4, socket.MSG_PEEK)
            if len(len_data) != 4:
                return None

        len_int = struct.unpack(">I", len_data)[0]
        len_int += 6
        self.buffer += self.s.recv(len_int - len(self.buffer))

        if len(self.buffer) != len_int:
            return None

        pkt = self.buffer
        self.buffer = b""
        return pkt

    def close(self):
        self.s.close()

