import socket
import struct

class HSFZSocket:
    def __init__(self, ip: str = '127.0.0.1', port: int = 6801) -> None:
        self.ip = ip
        self.port = port
        self.s = socket.socket(socket.AF_INET, socket.SOCK_STREAM)
        self.s.setsockopt(socket.IPPROTO_TCP, socket.TCP_NODELAY, 1)
        self.s.setsockopt(socket.SOL_SOCKET, socket.SO_REUSEADDR, 1)
        self.s.connect((self.ip, self.port))
        self.buffer = b''

    def send(self, data: bytes) -> int:
        return self.s.send(data)

    def recv(self, x: int = 65535) -> Optional[bytes]:
        if len(self.buffer) >= 4:
            length = struct.unpack('!I', self.buffer[:4])[0]
            if len(self.buffer) >= length + 4:
                data = self.buffer[4:length+4]
                self.buffer = self.buffer[length+4:]
                return data
        self.buffer += self.s.recv(x)
        return None

    def close(self) -> None:
        self.s.close()

class UDS_HSFZSocket(HSFZSocket):
    def __init__(self, ip: str = '127.0.0.1', port: int = 6801, source: int = 0, target: int = 0, outputcls: Type = None) -> None:
        super().__init__(ip, port)
        self.source = source
        self.target = target
        self.outputcls = outputcls

    def send(self, x: Any) -> int:
        data = x if isinstance(x, bytes) else x.pack()
        header = struct.pack('!IHH', len(data), 0, self.source, self.target)
        return self.s.send(header + data)

    def recv(self, x: int = 65535) -> Optional[Any]:
        data = super().recv(x)
        if data is None:
            return None
        length, flags, source, target = struct.unpack('!IHH', data[:8])
        if len(data) < length + 8:
            return None
        if self.outputcls is not None:
            return self.outputcls.unpack(data[8:8+length])
        return data[8:8+length]
