import socket
import pickle
from typing import Any, Optional, Union

CSPROTO = {
    'close': 0,
    'error': 1,
    'data': 2,
}

class CSocket:
    def __init__(self, sock: str, timeout: int = -1) -> None:
        self.__csock = socket.socket(socket.AF_UNIX, socket.SOCK_STREAM)
        self.__deftout = timeout
        self.__csock.connect(sock)
        if timeout != -1:
            self.settimeout(timeout)

    def __del__(self) -> None:
        self.close()

    def send(self, msg: Any, nonblocking: bool = False, timeout: Optional[float] = None) -> Any:
        if timeout is None:
            timeout = self.__deftout
        self.__csock.send(pickle.dumps((CSPROTO['data'], self.convert(msg))))
        return self.receive(self.__csock, nonblocking, timeout)

    def settimeout(self, timeout: float) -> None:
        self.__csock.settimeout(timeout)

    def close(self) -> None:
        self.__csock.send(pickle.dumps((CSPROTO['close'], None)))
        self.__csock.shutdown(socket.SHUT_RDWR)
        self.__csock.close()

    @staticmethod
    def convert(m: Any) -> Union[str, bool, int, float, list, dict, set]:
        if isinstance(m, (str, bool, int, float)):
            return m
        elif isinstance(m, (list, dict, set)):
            return m
        else:
            raise TypeError('Unsupported data type')

    @staticmethod
    def receive(sock: socket.socket, nonblocking: bool = False, timeout: Optional[float] = None) -> Any:
        if timeout is None:
            timeout = sock.gettimeout()
        if nonblocking:
            sock.setblocking(False)
        else:
            sock.setblocking(True)
        try:
            data = sock.recv(4096)
            if not data:
                return None
            return pickle.loads(data)[1]
        except socket.timeout:
            return None
        finally:
            if nonblocking:
                sock.setblocking(True)
