import socket

class RConConnection:
    def __init__(self, target: str, port: int) -> None:
        self.sock = socket.socket(socket.AF_INET, socket.SOCK_STREAM)
        self.sock.connect((target, port))
        self.rid = 1

    def send(self, t: int, payload: str) -> bytes:
        self.rid += 1
        data = self.rid.to_bytes(4, byteorder='little', signed=False) + t.to_bytes(4, byteorder='little', signed=False) + payload.encode('utf-8') + b'\x00\x00'
        self.sock.sendall(data)
        response = self.sock.recv(1024)
        return response

    def login(self, password: str) -> None:
        response = self.send(3, password)
        if response[8:12] != b'\x00\x00\x00\x00':
            raise Exception('Invalid password')

    def command(self, com: str, args: str) -> None:
        response = self.send(2, com + ' ' + args)
        if response[8:12] != b'\x00\x00\x00\x00':
            raise Exception('Command failed')

    def close(self) -> None:
        self.sock.close()
