import socket
import threading
import queue
import select

class ThreadedTCPClientSocket(threading.Thread):
    def __init__(self, ip, port, listener_q, remote_q, config, log):
        threading.Thread.__init__(self)
        self.ip = ip
        self.port = port
        self.listener_q = listener_q
        self.remote_q = remote_q
        self.config = config
        self.logger = log
        self.sock = socket.socket(socket.AF_INET, socket.SOCK_STREAM)
        self.sock.setsockopt(socket.IPPROTO_TCP, socket.TCP_NODELAY, 1)
        self.sock.setblocking(0)

    def connect(self):
        try:
            self.sock.connect((self.ip, self.port))
            return self.sock.getsockname()[1]
        except Exception as e:
            self.logger.error(f"Failed to connect to {self.ip}:{self.port}: {e}")
            return None

    def run(self):
        while True:
            try:
                readable, writable, exceptional = select.select([self.sock], [self.sock], [self.sock])
                if self.sock in readable:
                    data = self.sock.recv(4096)
                    if data:
                        self.listener_q.put(data)
                    else:
                        self.logger.info("Connection closed by server")
                        self.sock.close()
                        return
                if self.sock in writable:
                    if not self.remote_q.empty():
                        data = self.remote_q.get()
                        self.sock.sendall(data)
                if self.sock in exceptional:
                    self.logger.error("Socket exception")
                    self.sock.close()
                    return
            except Exception as e:
                self.logger.error(f"Error in main loop: {e}")
                self.sock.close()
                return
