        self.connection = namedtuple('connection', ['address', 'protocol', 'options'])
        self.connection = self.connection(connect_to, proto, headers)
        self.send_and_receive = self._stream_send_and_receive if proto in self.streams else self._dgram_send_and_receive
        self.on_unix = AF_UNIX in self.protocols.values()
        self.protocols = {'tcp': (AF_INET, SOCK_STREAM), 'udp': (AF_INET, SOCK_DGRAM), 'unix': (AF_UNIX, SOCK_STREAM), 'unix_dgram': (AF_UNIX, SOCK_DGRAM)}
        self.streams = {'tcp', 'unix'}
        self.datagrams = {'udp', 'unix_dgram'}
        self.inet = {'tcp', 'udp'}
        self.unix = {'unix', 'unix_dgram'}
        self.setsockopt(*kwargs.get('sockopts', ()))

    def settimeout(self, timeout: float) -> None:
        self.timeout = timeout

    def setsockopt(self, *sockopts: Tuple[Any, ...]) -> None:
        self.connection.options.update(sockopts)

    def _register_socket(self) -> socket:
        sock = socket(*self.protocols[self.connection.protocol])
        for opt, value in self.connection.options.items():
            sock.setsockopt(*opt, value)
        if self.timeout:
            sock.settimeout(self.timeout)
        return sock

    def _stream_send_and_receive(self, _socket: socket, message: str, *args, **kwargs) -> BytesIO:
        _socket.connect(self.connection.address)
        _socket.sendall(message.encode())
        data = _socket.recv(4096)
        _socket.close()
        return BytesIO(data)

    def _dgram_send_and_receive(self, _socket: socket, message: str, buffer_size: int = 4096, *args) -> BytesIO:
        _socket.connect(self.connection.address)
        _socket.sendall(message.encode())
        data = _socket.recv(buffer_size)
        _socket.close()
        return BytesIO(data)

    def request(self, message: str, timeout: bool = False, *args, **kwargs) -> BytesIO:
        if timeout:
            self.settimeout(timeout)
        if self.connection_pool.empty():
            sock = self._register_socket()
        else:
            sock = self.connection_pool.get()
        try:
            response = self.send_and_receive(sock, message, *args, **kwargs)
        except error as e:
            if e.errno in kwargs.get('raise_on', ()):
                raise
            response = BytesIO()
        finally:
            if not timeout:
                self.connection_pool.put(sock)
        return response
