import json
import logging
import urllib.parse
from typing import Generator

from letta import LLMError, LettaStreamingResponse, MessageStreamStatus, ReasoningMessage, ToolCallMessage, ToolReturnMessage, LettaUsageStatistics

logger = logging.getLogger(__name__)


def _sse_post(url: str, data: dict, headers: dict) -> Generator[LettaStreamingResponse, None, None]:
    import httpx
    import sse_starlette.sse

    def _sse_generator():
        try:
            with httpx.Client() as client:
                response = client.post(
                    url,
                    data=json.dumps(data),
                    headers=headers,
                    timeout=None,
                    stream=True,
                )
                response.raise_for_status()
                for event in sse_starlette.sse.EventSourceResponse(response):
                    if event.event == "message":
                        try:
                            message = json.loads(event.data)
                            if message["type"] == "reasoning":
                                yield ReasoningMessage(**message)
                            elif message["type"] == "tool_call":
                                yield ToolCallMessage(**message)
                            elif message["type"] == "tool_return":
                                yield ToolReturnMessage(**message)
                            elif message["type"] == "usage_statistics":
                                yield LettaUsageStatistics(**message)
                            else:
                                logger.warning(f"Unknown message type: {message['type']}")
                        except json.JSONDecodeError as e:
                            logger.error(f"Error parsing JSON: {e}")
                            raise LLMError("Error parsing JSON") from e
                    elif event.event == "status":
                        try:
                            status = json.loads(event.data)
                            yield MessageStreamStatus(**status)
                        except json.JSONDecodeError as e:
                            logger.error(f"Error parsing JSON: {e}")
                            raise LLMError("Error parsing JSON") from e
                    else:
                        logger.warning(f"Unknown event type: {event.event}")

        except httpx.HTTPStatusError as e:
            if e.response.status_code == 422:
                raise LLMError("Context window error") from e
            else:
                raise LLMError(f"HTTP error: {e}") from e
        except httpx.RequestError as e:
            raise LLMError(f"Request error: {e}") from e

    return _sse_generator()
