import socket
import select
import struct
import hashlib
import hmac
import base64
import os
import time
from Crypto.Cipher import AES
from Crypto.Util.Padding import pad, unpad

bind_host = '::'
bind_port = 6666
real_key = b'0123456789abcdef0123456789abcdef'

class TuyaServer:
    def __init__(self):
        self.sock = socket.socket(socket.AF_INET6, socket.SOCK_STREAM)
        self.sock.setsockopt(socket.SOL_SOCKET, socket.SO_REUSEADDR, 1)
        self.sock.bind((bind_host, bind_port))
        self.sock.listen(5)
        self.sock.setblocking(0)
        self.inputs = [self.sock]
        self.outputs = []
        self.message_queues = {}
        self.sessions = {}
        self.last_nonce = 0
        self.last_key = None

    def run(self):
        while self.inputs:
            readable, writable, exceptional = select.select(self.inputs, self.outputs, self.inputs)
            for s in readable:
                if s is self.sock:
                    self.accept()
                else:
                    data = s.recv(4096)
                    if data:
                        self.handle_message(s, data)
                    else:
                        self.close(s)
            for s in writable:
                try:
                    next_msg = self.message_queues[s].pop(0)
                except IndexError:
                    self.outputs.remove(s)
                else:
                    s.send(next_msg)
            for s in exceptional:
                self.close(s)

    def accept(self):
        conn, addr = self.sock.accept()
        conn.setblocking(0)
        self.inputs.append(conn)
        self.message_queues[conn] = []

    def handle_message(self, conn, data):
        if conn not in self.sessions:
            self.sessions[conn] = {'seq': 0, 'key': None, 'nonce': None}
        session = self.sessions[conn]
        if session['key'] is None:
            self.handle_negotiation(conn, data)
        else:
            self.handle_communication(conn, data)

    def handle_negotiation(self, conn, data):
        if data.startswith(b'SESS_KEY_NEG_START'):
            self.send_response(conn, b'SESS_KEY_NEG_START')
            self.send_response(conn, b'SESS_KEY_NEG_FINISH')
            session = self.sessions[conn]
            session['key'] = self.generate_key()
            session['nonce'] = os.urandom(16)
            self.send_response(conn, self.encrypt(session['nonce'], session['key']))
        elif data.startswith(b'SESS_KEY_NEG_FINISH'):
            session = self.sessions[conn]
            self.send_response(conn, self.encrypt(session['nonce'], session['key']))
        else:
            self.send_error(conn, b'Invalid message')

    def handle_communication(self, conn, data):
        session = self.sessions[conn]
        decrypted_data = self.decrypt(data, session['key'])
        if decrypted_data.startswith(b'DP_QUERY_NEW'):
            self.send_response(conn, self.encrypt(b'DP_QUERY_NEW', session['key']))
        else:
            self.send_error(conn, b'Invalid message')

    def send_response(self, conn, data):
        session = self.sessions[conn]
        encrypted_data = self.encrypt(data, session['key'])
        self.message_queues[conn].append(encrypted_data)

    def send_error(self, conn, error):
        self.message_queues[conn].append(error)

    def close(self, conn):
        if conn in self.inputs:
            self.inputs.remove(conn)
        if conn in self.outputs:
            self.outputs.remove(conn)
        if conn in self.message_queues:
            del self.message_queues[conn]
        if conn in self.sessions:
            del self.sessions[conn]
        conn.close()

    def generate_key(self):
        return os.urandom(16)

    def encrypt(self, data, key):
        iv = os.urandom(16)
        cipher = AES.new(key, AES.MODE_CBC, iv)
        encrypted_data = iv + cipher.encrypt(pad(data, AES.block_size))
        return encrypted_data

    def decrypt(self, data, key):
        iv = data[:16]
        cipher = AES.new(key, AES.MODE_CBC, iv)
        decrypted_data = unpad(cipher.decrypt(data[16:]), AES.block_size)
