    def __init__(self, host: str, port: int) -> None:
        self._closed = False
        self.host = host
        self.port = port
        self.sock = socket.socket(socket.AF_INET, socket.SOCK_STREAM)
        self.sock.bind((host, port))
        self.sock.listen()

    def accept(self) -> AcceptEvent:
        return AcceptEvent(self)

    def close(self) -> None:
        self._closed = True
        self.sock.close()

class Connection:
    def __init__(self, sock: socket.socket, addr: tuple) -> None:
        self.sock = sock
        self.addr = addr
        self._buf = b""
        self._closed = False

    def close(self) -> None:
        self._closed = True
        self.sock.close()

    def recv(self, size: int) -> ReceiveEvent:
        return ReceiveEvent(self, size)

    def send(self, data: bytes) -> SendEvent:
        return SendEvent(self, data)

    def sendall(self, data: bytes) -> SendEvent:
        return SendEvent(self, data, sendall=True)

    def readline(self, terminator: bytes = b"\n", bufsize: int = 1024) -> Generator[ReturnEvent, None, None]:
        while True:
            idx = self._buf.find(terminator)
            if idx != -1:
                line, self._buf = self._buf[:idx], self._buf[idx+1:]
                yield ReturnEvent(line)
            else:
                if len(self._buf) >= bufsize:
                    line, self._buf = self._buf[:bufsize], self._buf[bufsize:]
                    yield ReturnEvent(line)
                else:
                    yield from self.recv(bufsize)

class AcceptEvent(WaitableEvent):
    def __init__(self, listener: Listener) -> None:
        super().__init__()
        self.listener = listener

    def run(self) -> None:
        sock, addr = self.listener.sock.accept()
        conn = Connection(sock, addr)
        self.set(conn)

class ReceiveEvent(WaitableEvent):
    def __init__(self, conn: Connection, bufsize: int) -> None:
        super().__init__()
        self.conn = conn
        self.bufsize = bufsize

    def run(self) -> None:
        try:
            data = self.conn.sock.recv(self.bufsize)
            if not data:
                self.conn.close()
            self.set(data)
        except Exception as e:
            self.set_exception(e)

class SendEvent(WaitableEvent):
    def __init__(self, conn: Connection, data: bytes, sendall: bool = False) -> None:
        super().__init__()
        self.conn = conn
        self.data = data
        self.sendall = sendall

    def run(self) -> None:
        try:
            if self.sendall:
                sent = self.conn.sock.sendall(self.data)
            else:
                sent = self.conn.sock.send(self.data)
            self.set(sent)
        except Exception as e:
            self.set_exception(e)

class WaitableEvent:
    def __init__(self) -> None:
        self._future = asyncio.Future()

    def set(self, value: Any) -> None:
        self._future.set_result(value)

    def set_exception(self, exception: Exception) -> None:
        self._future.set_exception(exception)

    def wait(self) -> Any:
        return asyncio.run_coroutine_threadsafe(self._future, asyncio.get_event_loop()).result()
