import socket
import threading
import time

class MySocketServer:
    def __init__(self, host, port, name='SocketServer', numListen=1):
        self.host = host
        self.port = port
        self.name = name
        self.numListen = numListen
        self.sock = None

    def Run(self):
        try:
            self.sock = socket.socket(socket.AF_INET, socket.SOCK_STREAM)
            self.sock.setsockopt(socket.SOL_SOCKET, socket.SO_REUSEADDR, 1)
            self.sock.bind((self.host, self.port))
            self.sock.listen(self.numListen)
            self.onStart()
            while True:
                try:
                    sock, addr = self.sock.accept()
                    threading.Thread(target=self.onAccept, args=(sock, addr)).start()
                except Exception as e:
                    self.onAcceptError(e)
        except Exception as e:
            self.onStartFail(e)
        finally:
            self.onStop()

    def Stop(self):
        if self.sock:
            self.sock.close()

    def onAccept(self, sock, addr):
        try:
            data = sock.recv(1024)
            if data:
                threading.Thread(target=self.onData, args=(sock, addr, data)).start()
            else:
                sock.close()
        except Exception as e:
            self.onRecvError(sock, addr, e)

    def onData(self, sock, addr, data):
        try:
            resp = self.response(data)
            sock.sendall(resp)
        except Exception as e:
            self.onSendError(sock, addr, e)
        finally:
            sock.close()

    def response(self, data):
        return b'Received: ' + data

    def onStartFail(self, e):
        print(f'{self.name} failed to start: {e}')

    def onStart(self):
        print(f'{self.name} started on {self.host}:{self.port}')

    def onAcceptError(self, e):
        print(f'Error accepting connection: {e}')

    def onRecvError(self, sock, addr, e):
        print(f'Error receiving data from {addr}: {e}')

    def onSendError(self, sock, addr, e):
        print(f'Error sending data to {addr}: {e}')

    def onStop(self):
        print(f'{self.name} stopped')

    @staticmethod
    def Query(host, port, req):
        sock = socket.socket(socket.AF_INET, socket.SOCK_STREAM)
        try:
            sock.connect((host, port))
            sock.sendall(req)
            data = sock.recv(1024)
            return data
        except Exception as e:
            print(f'Query error: {e}')
        finally:
            sock.close()
