import socket
import select

class TCPForwardServer:
    def __init__(self, lhost: str, lport: int, rdev: Device, rport: int) -> None:
        self.input_list = []
        self.channel = {}
        self._server = socket.socket(socket.AF_INET, socket.SOCK_STREAM)
        self._server.setsockopt(socket.SOL_SOCKET, socket.SO_REUSEADDR, 1)
        self._server.bind((lhost, lport))
        self._server.listen(5)
        self._server.setblocking(0)
        self.input_list.append(self._server)
        self._rdev = rdev
        self._rport = rport
        self.BUFFER_SIZE = 4096
        self.DELAY = 0.0001

    def main_loop(self) -> None:
        while self.input_list:
            inputready, outputready, exceptready = select.select(self.input_list, [], [], self.DELAY)
            for s in inputready:
                if s == self._server:
                    self.on_accept()
                elif self.channel[s] == self._rdev:
                    self.on_recv(s, self.channel[s])
                else:
                    self.on_recv(self.channel[s], s)

    def on_accept(self) -> None:
        clientsock, clientaddr = self._server.accept()
        clientsock.setblocking(0)
        self.input_list.append(clientsock)
        try:
            devsock = self._rdev.connect((self._rport))
            devsock.setblocking(0)
            self.input_list.append(devsock)
            self.channel[clientsock] = devsock
            self.channel[devsock] = clientsock
        except Exception as e:
            print(f"Failed to connect to device: {e}")
            clientsock.close()

    def on_close(self, sock: socket.socket) -> None:
        if sock in self.input_list:
            self.input_list.remove(sock)
        if sock in self.channel:
            peer = self.channel[sock]
            self.channel.pop(sock)
            self.channel.pop(peer)
            peer.close()

    def on_recv(self, sock: socket.socket, peer: socket.socket) -> None:
        try:
            data = sock.recv(self.BUFFER_SIZE)
            if data:
                peer.send(data)
            else:
                self.on_close(sock)
        except Exception as e:
            print(f"Failed to receive data: {e}")
            self.on_close(sock)
