import asyncio
import websockets
from typing import Any, Optional, Union, List, Tuple
from .jsonrpc import JSONBaseProvider, RPCEndpoint, RPCResponse

class PersistentWebSocket:
    def __init__(self, endpoint_uri: str, websocket_kwargs: Any = None):
        self.ws: Optional[websockets.WebSocketClientProtocol] = None
        self.endpoint_uri = endpoint_uri
        self.websocket_kwargs = websocket_kwargs or {}

    async def __aenter__(self) -> websockets.WebSocketClientProtocol:
        if self.ws is None:
            self.ws = await websockets.connect(self.endpoint_uri, **self.websocket_kwargs)
        return self.ws

    async def __aexit__(self, exc_type: type, exc_val: BaseException, exc_tb: Any) -> None:
        if self.ws:
            await self.ws.close()
            self.ws = None

class LegacyWebSocketProvider(JSONBaseProvider):
    _loop: ClassVar[Any] = asyncio.get_event_loop()

    def __init__(self, endpoint_uri: Optional[Union[str, "URI"]] = None, websocket_timeout: int = 10):
        super().__init__(endpoint_uri, websocket_timeout)
        self.conn = PersistentWebSocket(self.endpoint_uri)

    async def coro_make_request(self, request_data: bytes) -> RPCResponse:
        async with self.conn as ws:
            await ws.send(request_data)
            response_data = await asyncio.wait_for(ws.recv(), self.websocket_timeout)
        return self.parse_response(response_data)

    def make_request(self, method: RPCEndpoint, params: Any) -> RPCResponse:
        request_data = self.create_request(method, params)
        return self._loop.run_until_complete(self.coro_make_request(request_data))

