import zmq
import numpy as np
from typing import Optional, Union, List, Generator

class BertClient:
    def __init__(self, ip='localhost', port=5555, port_out=5556, identity=None, timeout=-1):
        self.context = zmq.Context()
        self.sender = self.context.socket(zmq.PUSH)
        self.sender.connect(f'tcp://{ip}:{port}')
        self.receiver = self.context.socket(zmq.SUB)
        self.receiver.connect(f'tcp://{ip}:{port_out}')
        self.receiver.setsockopt_string(zmq.SUBSCRIBE, '')
        self.identity = identity.encode() if identity else None
        self.request_id = 0
        self.timeout = timeout
        self.pending_request = set()

    def close(self):
        self.sender.close()
        self.receiver.close()
        self.context.term()

    def _send(self, msg, msg_len=0):
        if self.identity:
            msg = self.identity + msg
        self.sender.send(msg, msg_len, zmq.NOBLOCK)

    def _recv(self):
        msg = self.receiver.recv()
        if self.identity:
            msg = msg[len(self.identity):]
        return msg

    def _recv_ndarray(self):
        msg = self._recv()
        return np.frombuffer(msg, dtype=np.float32)

    def encode(self, texts, blocking=True):
        self.request_id += 1
        request_id = self.request_id
        self.pending_request.add(request_id)
        self._send(f'{request_id} {texts}'.encode(), zmq.SNDMORE)
        if blocking:
            while request_id in self.pending_request:
                yield from self.fetch()
        else:
            return None

    def fetch(self, delay=0.0):
        if delay > 0.0:
            yield from self._fetch_delayed(delay)
        else:
            yield from self._fetch()

    def _fetch(self):
        while self.pending_request:
            msg = self._recv()
            request_id = int(msg.split()[0])
            if request_id in self.pending_request:
                self.pending_request.remove(request_id)
                yield Response(request_id, self._recv_ndarray())

    def _fetch_delayed(self, delay):
        while self.pending_request:
            msg = self._recv()
            request_id = int(msg.split()[0])
            if request_id in self.pending_request:
                self.pending_request.remove(request_id)
                yield Response(request_id, self._recv_ndarray())
            yield delay

    def fetch_all(self, sort=True, concat=False):
        responses = list(self.fetch())
        if sort:
            responses.sort(key=lambda r: r.request_id)
        if concat:
            return np.concatenate([r.data for r in responses])
        return [r.data for r in responses]

class Response:
    def __init__(self, request_id, data):
        self.request_id = request_id
        self.data = data
