import sys
import time

def server(channel_ip=None, channel_port=5555):
    context = zmq.Context()
    socket = context.socket(zmq.REP)
    if channel_ip is None:
        socket.bind("tcp://*:{}".format(channel_port))
    else:
        socket.bind("tcp://{}:{}".format(channel_ip, channel_port))

    remaining_entries = [1, 2, 3, 4, 5]  # Example task IDs
    running_workers = 0

    while remaining_entries or running_workers:
        message = socket.recv()
        if message == b"READY":
            if remaining_entries:
                task_id = remaining_entries.pop(0)
                socket.send(str(task_id).encode())
                running_workers += 1
            else:
                socket.send(b"NONE")
        elif message == b"SUCCESS":
            running_workers -= 1
        elif message == b"FAILURE":
            running_workers -= 1
            remaining_entries.append(1)  # Re-add failed task ID for retry
        else:
            socket.send(b"UNKNOWN")

        time.sleep(0.1)  # Prevent busy waiting

    socket.close()
    context.term()

if __name__ == "__main__":
    channel_ip = None
    channel_port = 5555
    if len(sys.argv) > 1:
        channel_ip = sys.argv[1]
    if len(sys.argv) > 2:
        channel_port = int(sys.argv[2])
