class MockEstopServicer(estop_pb2_grpc.EstopServiceServicer):
    VALID_STOP_LEVEL = 1
    NAME_FOR_ENDPOINT_UNKNOWN = 'unknown'
    NAME_FOR_SERVER_ERROR = 'server_error'
    STATUSES_THAT_DO_NOT_PROVIDE_CHALLENGE = {'ok', 'error', 'unknown'}

    def __init__(self, rpc_delay: int = 0):
        self._rpc_delay = rpc_delay
        self._challenge = 0

    def RegisterEstopEndpoint(self, request, context):
        pass

    def EstopCheckIn(self, request, context):
        if request.endpoint_name == self.NAME_FOR_ENDPOINT_UNKNOWN:
            return estop_pb2.EstopCheckInResponse(status=estop_pb2.EstopEndpointStatus.UNKNOWN)
        elif request.endpoint_name == self.NAME_FOR_SERVER_ERROR:
            raise grpc.RpcError(grpc.StatusCode.INTERNAL, 'Server error')
        if request.stop_level != self.VALID_STOP_LEVEL:
            return estop_pb2.EstopCheckInResponse(status=estop_pb2.EstopEndpointStatus.INVALID_STOP_LEVEL)
        if request.endpoint_name not in self.STATUSES_THAT_DO_NOT_PROVIDE_CHALLENGE:
            self._challenge += 1
            return estop_pb2.EstopCheckInResponse(status=estop_pb2.EstopEndpointStatus.OK, challenge=self._challenge)
        return estop_pb2.EstopCheckInResponse(status=estop_pb2.EstopEndpointStatus.OK)

