        self._stop_event = threading.Event()
        self.inference_pipeline = async_pipeline or async_pipeline.AsyncPipeline()
        self.method_name_to_task = {
            'GeneratorReply': 'batch',
            'GeneratorReplyStream': 'stream'
        }
        self.lock = threading.Lock()

    def Terminate(self, request: model_pb2.Empty, context: grpc.ServicerContext) -> model_pb2.Empty:
        self._stop_event.set()
        return model_pb2.Empty()

    def get_stop_event(self) -> threading.Event:
        return self._stop_event

    def GeneratorReply(self, request: model_pb2.ModelRequest, context: grpc.ServicerContext) -> model_pb2.ModelResponse:
        task_methods = self._get_task_methods('GeneratorReply')
        return task_methods.process_request(request, context)

    def GeneratorReplyStream(self, request: model_pb2.ModelRequest, context: grpc.ServicerContext) -> Iterator[model_pb2.ModelResponse]:
        task_methods = self._get_task_methods('GeneratorReplyStream')
        return task_methods.process_request(request, context)

    def _get_task_methods(self, method_name: str) -> task_methods.TaskMethods:
        task_type = self.method_name_to_task[method_name]
        return task_methods.TaskMethods(task_type, self.inference_pipeline, self.lock)

def _do_serve(service_impl: ModelResponse, port: int, interceptors: List[grpc.ServerInterceptor] = None):
