            return "Error: API key not provided"

        location_key = self.get_location_key(city)
        if not location_key:
            return "Error: Unable to retrieve location key"

        if extensions == "all":
            url = f"http://dataservice.accuweather.com/currentconditions/v1/{location_key}?apikey={self.api_key}"
        elif extensions == "base":
            url = f"http://dataservice.accuweather.com/forecasts/v1/daily/5day/{location_key}?apikey={self.api_key}"
        else:
            return "Error: Invalid extensions parameter"

        response = requests.get(url)
        if response.status_code != 200:
            return f"Error: {response.status_code} - {response.text}"

        return response.json()

    def get_location_key(self, city: str) -> str:
        url = f"http://dataservice.accuweather.com/locations/v1/cities/search?apikey={self.api_key}&q={city}"
        response = requests.get(url)
        if response.status_code != 200:
            return ""

        data = response.json()
        if not data:
            return ""

        return data[0]["Key"]
