from http.client import HTTPConnection
from queue import LifoQueue
from typing import Optional, Dict, Any
from urllib.parse import urlparse
from urllib3.connection import HTTPConnection as HTTPConnectionBase
from urllib3.connectionpool import HTTPConnectionPool as HTTPConnectionPoolBase
from urllib3.exceptions import ConnectTimeoutError, MaxRetryError
from urllib3.util.timeout import Timeout
from urllib3.util.retry import Retry

class HTTPConnectionPool(HTTPConnectionPoolBase):
    def __init__(self, host, port=None, strict=False, timeout=Timeout.DEFAULT_TIMEOUT, maxsize=1, block=False, headers=None, retries=None, _proxy=None, _proxy_headers=None, **conn_kw):
        super().__init__(host, port, strict, timeout, maxsize, block, headers, retries, _proxy, _proxy_headers, **conn_kw)
        self.pool = LifoQueue(maxsize)
        self.block = block

    def _new_conn(self):
        return self.ConnectionCls(self.host, self.port, strict=self.strict, timeout=self.timeout, **self.conn_kw)

    def _get_conn(self, timeout=None):
        if self.pool.empty():
            conn = self._new_conn()
        else:
            try:
                conn = self.pool.get(block=self.block, timeout=timeout)
            except queue.Empty:
                conn = self._new_conn()
        return conn

    def _put_conn(self, conn):
        self.pool.put(conn)

    def _make_request(self, conn, method, url, timeout=_Default, **httplib_request_kw):
        try:
            conn.request(method, url, **httplib_request_kw)
            return conn.getresponse()
        except (ConnectTimeoutError, MaxRetryError) as e:
            if self.retries is None or not self.retries.is_retry(e):
                raise
            conn.close()
            conn = self._new_conn()
            return self._make_request(conn, method, url, timeout, **httplib_request_kw)

    def urlopen(self, method, url, body=None, headers=None, retries=None, redirect=True, timeout=_Default, **response_kw):
        retries = retries or self.retries
        if retries is not None:
            retries = retries.clone()

        parsed_url = urlparse(url)
        if parsed_url.scheme not in ('http', 'https'):
            raise ValueError("Unsupported URL scheme: %s" % parsed_url.scheme)

        conn = self._get_conn(timeout=timeout)
        try:
            response = self._make_request(conn, method, url, timeout, body=body, headers=headers, **response_kw)
            if redirect and response.status in (301, 302, 303, 307, 308):
                location = response.getheader('Location')
                if location is None:
                    raise ValueError("Missing Location header in redirect")
                return self.urlopen(method, location, body=body, headers=headers, retries=retries, redirect=False, timeout=timeout, **response_kw)
            return response
        finally:
            self._put_conn(conn)

    def close(self):
        while not self.pool.empty():
            conn = self.pool.get(block=False)
            conn.close()
