import requests
import json

class AmazonSSO:
    def __init__(self, access_token: str = None, refresh_token: str = None):
        self.access_token = access_token
        self.refresh_token = refresh_token
        self.client_id = 'your_client_id'
        self.client_secret = 'your_client_secret'
        self.user_pool_id = 'your_user_pool_id'
        self.region = 'your_region'
        self.user_info = {}

    def get_new_token(self) -> str:
        url = f'https://cognito-idp.{self.region}.amazonaws.com/{self.user_pool_id}'
        headers = {'Content-Type': 'application/x-www-form-urlencoded'}
        data = {
            'grant_type': 'refresh_token',
            'client_id': self.client_id,
            'client_secret': self.client_secret,
            'refresh_token': self.refresh_token
        }
        response = requests.post(url, headers=headers, data=data)
        if response.status_code == 200:
            tokens = response.json()
            self.access_token = tokens['access_token']
            self.refresh_token = tokens['refresh_token']
            return self.access_token
        else:
            return None

    def get_user_info(self) -> dict:
        if not self.access_token:
            return {}
        url = f'https://cognito-idp.{self.region}.amazonaws.com/{self.user_pool_id}/.well-known/openid-configuration'
        response = requests.get(url)
        if response.status_code == 200:
            jwks_uri = response.json()['jwks_uri']
            response = requests.get(jwks_uri)
            if response.status_code == 200:
                keys = response.json()['keys']
                for key in keys:
                    if key['kid'] == 'your_kid':
                        n = int(key['n'], 16)
                        e = int(key['e'], 16)
                        modulus = pow(10, len(str(n)), n)
                        exponent = pow(10, len(str(e)), e)
                        encoded_token = self.access_token.split('.')[1]
                        decoded_token = pow(int(encoded_token, 16), exponent, modulus)
                        self.user_info = json.loads(bytes.fromhex(hex(decoded_token)[2:]).decode())
                        return self.user_info
        return {}

    @staticmethod
    def amazon_sso(code: str, redirect_uri: str = None) -> tuple[AmazonSSO, None] | tuple[None, None]:
        if not redirect_uri:
            redirect_uri = 'your_redirect_uri'
        url = f'https://cognito-idp.{AmazonSSO.region}.amazonaws.com/{AmazonSSO.user_pool_id}'
        headers = {'Content-Type': 'application/x-www-form-urlencoded'}
        data = {
            'grant_type': 'authorization_code',
            'client_id': AmazonSSO.client_id,
            'client_secret': AmazonSSO.client_secret,
            'code': code,
            'redirect_uri': redirect_uri
        }
        response = requests.post(url, headers=headers, data=data)
        if response.status_code == 200:
            tokens = response.json()
            return AmazonSSO(tokens['access_token'], tokens['refresh_token']), None
        else:
            return None, None
