import logging
import os
import threading
import time
import typing
import requests

from threading import Thread, Event
from typing import Dict, Optional
from tenacity import (
    RetryError,
    retry,
    stop_after_attempt,
    wait_exponential,
    retry_if_exception,
)

class Fetcher:
    def __init__(self, base_url: str, api_key: str):
        self._base_url = base_url
        self._api_key = api_key
        self._stop_polling_event = Event()
        self._exit_monitor = Thread(
            target=self._monitor_exit, args=(self._stop_polling_event,), daemon=True
        )
        self._poller_thread = Thread(
            target=self._thread_func,
            args=(
                self._base_url,
                self._api_key,
                self._stop_polling_event,
                int(os.getenv("TRACELOOP_PROMPT_MANAGER_POLLING_INTERVAL") or 5),
            ),
        )

    def run(self):
        self._refresh_data(self._base_url, self._api_key)
        self._exit_monitor.start()
        self._poller_thread.start()

    def post(self, api: str, body: Dict[str, str]):
        self._post_url(f"{self._base_url}/v1/traceloop/{api}", self._api_key, body)

    @retry(
        wait=wait_exponential(multiplier=1, min=4),
        stop=stop_after_attempt(int(os.getenv("TRACELOOP_PROMPT_MANAGER_MAX_RETRIES") or 3)),
        retry=retry_if_exception(lambda e: isinstance(e, requests.exceptions.HTTPError) and (500 <= e.response.status_code < 600),
    )
    def _fetch_url(self, url: str, api_key: str):
        response = requests.get(
            url,
            headers={
                "Authorization": f"Bearer {api_key}",
                "X-Traceloop-SDK-Version": "1.0.0",
            },
        )

        if response.status_code != 200:
            if response.status_code == 401 or response.status_code == 403:
                logging.error("Authorization error: Invalid Traceloop API key.")
                raise requests.exceptions.HTTPError(response=response)
            else:
                logging.error("Request failed: %s", response.status_code)
                raise requests.exceptions.HTTPError(response=response)
        else:
            return response.json()

    def _post_url(self, url: str, api_key: str, body: Dict[str, str]):
        response = requests.post(
            url,
            headers={
                "Authorization": f"Bearer {api_key}",
                "X-Traceloop-SDK-Version": "1.0.0",
            },
            json=body,
        )

        if response.status_code != 200:
            raise requests.exceptions.HTTPError(response=response)

    def _thread_func(self, base_url: str, api_key: str, stop_polling_event: Event, seconds_interval: Optional[int] = 5):
        while not stop_polling_event.is_set():
            try:
                self._refresh_data(base_url, api_key)
            except RetryError:
                logging.error("Request failed after retries : stopped polling")
                break

            time.sleep(seconds_interval)

    def _refresh_data(self, base_url: str, api_key: str):
        self._fetch_url(f"{base_url}/v1/traceloop/prompts", api_key)
        self._fetch_url(f"{base_url}/v1/traceloop/pii/tracing-allow-list", api_key)

    def _monitor_exit(self, exit_event: Event):
        main_thread = threading.main_thread()
        main_thread.join()
        exit_event.set()

