import requests
import urllib.parse
import json

class PulsstoryHTTPClient:
    def __init__(self, base_url, client_id=None, client_secret=None):
        self.URLBase = base_url
        self.PULSSTORY_CLIENT_ID = client_id or PULSSTORY_CLIENT_ID
        self.PULSSTORY_CLIENT_SECRET = client_secret or PULSSTORY_CLIENT_SECRET

    def _api_request(self, endpoint, service_record=None, method="POST", data=None, headers=None, files=None):
        url = self.URLBase + endpoint
        request_data = self._apiData(service_record) if service_record else {}
        if data:
            request_data.update(data)

        if method == "POST":
            response = requests.post(url, data=request_data, headers=headers, files=files)
        else:
            raise ValueError("Unsupported HTTP method")

        if response.status_code != 200:
            if response.status_code in [401, 403]:
                raise APIException("No authorization for request", block=True, user_exception=UserException(UserExceptionType.Authorization, intervention_required=True))
            raise APIException(f"Request failed with status {response.status_code}: {response.text}")

        return response.json()

    def _apiData(self, service_record):
        return {"access_token": service_record.Authorization["Token"]}

    def get_user_id(self, service_record):
        return self._api_request("/ExternalSyncAPI/GetUserId", service_record)["userID"]

    def get_api_uris(self, service_record):
        uris = self._api_request("/ExternalSyncAPI/Uris", service_record)
        for k in uris.keys():
            if isinstance(uris[k], str):
                uris[k] = self.URLBase + uris[k]
        return uris

    def retrieve_authorization_token(self, code):
        params = {
            "code": code,
            "client_id": self.PULSSTORY_CLIENT_ID,
            "client_secret": self.PULSSTORY_CLIENT_SECRET
        }
        response = requests.post(
            self.URLBase + "/ExternalSyncAPI/GenerateToken",
            data=urllib.parse.urlencode(params),
            headers={"Content-Type": "application/x-www-form-urlencoded"}
        )
        if response.status_code != 200:
            raise APIException("Invalid code")
        return response.json()["access_token"]

    def revoke_authorization(self, service_record):
        response = requests.post(self.URLBase + "/ExternalSyncAPI/Deauthorize", data=self._apiData(service_record))
        if response.status_code not in [200, 204]:
            raise APIException(f"Unable to deauthorize token, status {response.status_code}")

    def download_activity(self, service_record, activity_id):
        return self._api_request(activity_id, service_record)

    def upload_activity(self, service_record, upload_data, uris):
        json_data = json.dumps(upload_data)
        buffer = io.BytesIO()
        with zipfile.ZipFile(buffer, 'w') as myzip:
            myzip.writestr('activity.txt', json_data, compress_type=zipfile.ZIP_DEFLATED)
        files = {"data": buffer.getvalue()}
        return self._api_request(uris["upload_activity_zip"], service_record, files=files)["Id"]

