class Timeout:
    DEFAULT_TIMEOUT = 10

class Retry:
    DEFAULT_RETRY = 3

class HTTPConnectionPool:
    def __init__(self, host: str, port: Optional[int] = None, strict: bool = False, timeout: Timeout = Timeout.DEFAULT_TIMEOUT, maxsize: int = 1, block: bool = False, headers: Optional[Dict[str, str]] = None, retries: Retry = Retry.DEFAULT_RETRY, _proxy: Optional[str] = None, _proxy_headers: Optional[Dict[str, str]] = None, **conn_kw: Any) -> None:
        self.scheme = 'http'
        self.ConnectionCls = HTTPConnection
        self.ResponseCls = HTTPResponse
        self.host = host
        self.port = port
        self.timeout = timeout
        self.retries = retries
        self.pool = queue.LifoQueue(maxsize)
        self.block = block
        self.proxy = _proxy
        self.proxy_headers = _proxy_headers if _proxy_headers else {}
        self.conn_kw = conn_kw

    def _new_conn(self) -> HTTPConnection:
        conn = self.ConnectionCls(self.host, self.port, timeout=self.timeout, **self.conn_kw)
        return conn

    def _get_conn(self, timeout: Optional[int] = None) -> HTTPConnection:
        if self.pool.empty():
            return self._new_conn()
        return self.pool.get()

    def _put_conn(self, conn: HTTPConnection) -> None:
        if self.pool.full():
            conn.close()
        else:
            self.pool.put(conn)

    def _make_request(self, conn: HTTPConnection, method: str, url: str, timeout: Timeout = Timeout.DEFAULT_TIMEOUT, **httplib_request_kw: Any) -> HTTPResponse:
        conn.request(method, url, **httplib_request_kw)
        response = conn.getresponse()
        return response

    def urlopen(self, method: str, url: str, body: Optional[bytes] = None, headers: Optional[Dict[str, str]] = None, retries: Retry = Retry.DEFAULT_RETRY, redirect: bool = True, assert_same_host: bool = True, timeout: Timeout = Timeout.DEFAULT_TIMEOUT, pool_timeout: Optional[int] = None, release_conn: Optional[bool] = None, **response_kw: Any) -> HTTPResponse:
        conn = self._get_conn(timeout=pool_timeout)
        try:
            response = self._make_request(conn, method, url, timeout=timeout, **response_kw)
            if response.status == HTTPStatus.MOVED_PERMANENTLY or response.status == HTTPStatus.MOVED_TEMPORARILY:
                if redirect:
                    new_url = response.getheader('Location')
                    return self.urlopen(method, new_url, body=body, headers=headers, retries=retries, redirect=False, assert_same_host=assert_same_host, timeout=timeout, pool_timeout=pool_timeout, release_conn=release_conn, **response_kw)
            return response
        finally:
            if release_conn:
                self._put_conn(conn)
