class CloudflareAPI:
    def __init__(self, auth_email=None, api_key=None, proxy=None, ttl=None):
        self.ID = auth_email
        self.TOKEN = api_key
        self.PROXY = proxy
        self.TTL = ttl
        self.SITE = "api.cloudflare.com"
        self._records_cache = {}

    def _request(self, method, action, param=None, **params):
        if param:
            params.update(param)

        params = dict((k, params[k]) for k in params if params[k] is not None)
        info("%s/%s : %s", self.SITE, action, params)

        if self.PROXY:
            conn = HTTPSConnection(self.PROXY)
            conn.set_tunnel(self.SITE, 443)
        else:
            conn = HTTPSConnection(self.SITE)

        if method in ['PUT', 'POST', 'PATCH']:
            params['content'] = str(params.get('content'))
            params = jsonencode(params)
        else:
            if params:
                action += '?' + urlencode(params)
            params = None

        if not self.ID:
            headers = {"Content-type": "application/json",
                      "Authorization": "Bearer " + self.TOKEN}
        else:
            headers = {"Content-type": "application/json",
                      "X-Auth-Email": self.ID, "X-Auth-Key": self.TOKEN}

        conn.request(method, '/client/v4/zones' + action, params, headers)
        response = conn.getresponse()
        res = response.read().decode('utf8')
        conn.close()

        if response.status < 200 or response.status >= 300:
            warning('%s : error[%d]:%s', action, response.status, res)
            raise Exception(res)
        else:
            data = jsondecode(res)
            debug('%s : result:%s', action, data)
            if not data:
                raise Exception("Empty Response")
            elif data.get('success'):
                return data.get('result', [{}])
            else:
                raise Exception(data.get('errors', [{}]))

    def get_zone_id(self, domain):
        zoneid = None
        domain_slice = domain.split('.')
        index = 2
        while (not zoneid) and (index <= len(domain_slice)):
            zones = self._request('GET', '', name='.'.join(domain_slice[-index:]))
            zone = next((z for z in zones if domain.endswith(z.get('name'))), None)
            zoneid = zone and zone['id']
            index += 1
        return zoneid

    def get_records(self, zoneid, **conditions):
        cache_key = zoneid + "_" + conditions.get('name', "") + "_" + conditions.get('type', "")
        if cache_key not in self._records_cache:
            self._records_cache[cache_key] = {}
            data = self._request('GET', '/' + zoneid + '/dns_records', per_page=100, **conditions)
            if data:
                for record in data:
                    self._records_cache[cache_key][record['id']] = {
                        'id': record['id'],
                        'type': record['type'],
                        'name': record['name'],
                        'content': record['content'],
                        'proxied': record['proxied'],
                        'ttl': record['ttl']
                    }

        records = {}
        for (zid, record) in self._records_cache[cache_key].items():
            for (k, value) in conditions.items():
                if record.get(k) != value:
                    break
            else:
                records[zid] = record
        return records

    def update_record(self, domain, value, record_type="A"):
        zoneid = self.get_zone_id(domain)
        if not zoneid:
            raise Exception("invalid domain: [ %s ] " % domain)

        records = self.get_records(zoneid, name=domain, type=record_type)
        cache_key = zoneid + "_" + domain + "_" + record_type
        result = {}

