class Timeout:
    DEFAULT_TIMEOUT = 10

class Retry:
    DEFAULT_RETRIES = 3

class HTTPConnectionPool:
    def __init__(self, host, port=None, strict=False, timeout=Timeout.DEFAULT_TIMEOUT, maxsize=1, block=False, headers=None, retries=None, _proxy=None, _proxy_headers=None, **conn_kw):
        self.scheme = urlparse(host).scheme
        self.host = urlparse(host).hostname
        self.port = urlparse(host).port
        self.timeout = timeout
        self.retries = retries or Retry.DEFAULT_RETRIES
        self.pool = LifoQueue(maxsize=maxsize)
        self.block = block
        self.proxy = _proxy
        self.proxy_headers = _proxy_headers or {}
        self.conn_kw = conn_kw

    def _new_conn(self):
        connection = http.client.HTTPConnection(self.host, self.port, timeout=self.timeout, **self.conn_kw)
        return connection

    def _get_conn(self, timeout=None):
        if self.pool.empty():
            if self.block:
                return self.pool.get(timeout=timeout)
            else:
                return self._new_conn()
        return self.pool.get()

    def _put_conn(self, conn):
        self.pool.put(conn)

    def _make_request(self, conn, method, url, timeout=_Default, **httplib_request_kw):
        conn.request(method, url, **httplib_request_kw)
        response = conn.getresponse()
        return response

    def urlopen(self, method, url, body=None, headers=None, retries=None, redirect=True, assert_same_host=True, timeout=_Default, pool_timeout=None, release_conn=None, **response_kw):
        retries = retries or self.retries
        conn = self._get_conn(timeout=pool_timeout)
        try:
            response = self._make_request(conn, method, url, timeout=timeout, **response_kw)
            if redirect and response.status in (301, 302, 303, 307, 308):
                new_url = response.getheader('Location')
                if assert_same_host and urlparse(new_url).hostname != urlparse(url).hostname:
                    raise ValueError("Redirect to a different host")
                return self.urlopen(method, new_url, body=body, headers=headers, retries=retries, redirect=False, assert_same_host=assert_same_host, timeout=timeout, pool_timeout=pool_timeout, release_conn=release_conn, **response_kw)
            return response
        finally:
            if release_conn is None or release_conn:
                self._put_conn(conn)

    def close(self):
        while not self.pool.empty():
            conn = self.pool.get()
            conn.close()

class HTTPSConnectionPool(HTTPConnectionPool):
    def __init__(self, host, port=None, strict=False, timeout=Timeout.DEFAULT_TIMEOUT, maxsize=1, block=False, headers=None, retries=None, _proxy=None, _proxy_headers=None, key_file=None, cert_file=None, cert_reqs='CERT_NONE', ca_certs=None, ssl_version=None, assert_hostname=None, assert_fingerprint=None, **conn_kw):
        super().__init__(host, port=port, strict=strict, timeout=timeout, maxsize=maxsize, block=block, headers=headers, retries=retries, _proxy=_proxy, _proxy_headers=_proxy_headers, **conn_kw)
        self.key_file = key_file
        self.cert_file = cert_file
        self.cert_reqs = cert_reqs
        self.ca_certs = ca_certs
        self.ssl_version = ssl_version
        self.assert_hostname = assert_hostname
        self.assert_fingerprint = assert_fingerprint

    def _prepare_conn(self, conn):
        context = ssl.create_default_context(ssl.Purpose.SERVER_AUTH, cafile=self.ca_certs, certfile=self.cert_file, keyfile=self.key_file, cert_reqs=self.cert_reqs, ssl_version=self.ssl_version)
        if self.assert_hostname:
            context.check_hostname = True
        if self.assert_fingerprint:
            conn.set_context(context)
        return conn

    def _new_conn(self):
        connection = http.client.HTTPSConnection(self.host, self.port, timeout=self.timeout, context=self._prepare_conn(ssl.create_default_context()), **self.conn_kw)
        return connection
