        self.app.add_route(self._graphql_request, "/graphql", methods=["GET", "POST"])

    def _graphql_request(self, request: Request) -> Response:
        method = request.method.lower()
        query = request.query_args.get("query")
        variables = request.json.get("variables")
        files = request.files if self.multipart_uploads_enabled else None
        headers = request.headers
        return self._graphql_request(method, query, variables, files, headers)

    def _graphql_request(self, method: Literal["get", "post"], query: Optional[str], variables: Optional[Dict[str, object]], files: Optional[Dict[str, BytesIO]], headers: Optional[Dict[str, str]], **kwargs: Any) -> Response:
        url = "http://example.com/graphql"
        data = {"query": query, "variables": variables}
        if method == "get" and self.allow_queries_via_get:
            return response.text(json.dumps(data), content_type="application/json")
        elif method == "post":
            if files:
                fields = {"query": query, "variables": json.dumps(variables)}
                encoder = MultipartEncoder(fields=fields, files=files)
                headers = {"Content-Type": encoder.content_type}
                return response.text(encoder.read(), content_type=headers["Content-Type"])
            else:
                return response.text(json.dumps(data), content_type="application/json")
        else:
            return response.text(json.dumps({"error": "Unsupported method"}), content_type="application/json")

    def request(self, url: str, method: Literal["get", "post", "patch", "put", "delete"], headers: Optional[Dict[str, str]]) -> Response:
        response = requests.request(method, url, headers=headers)
        return response

    def get(self, url: str, headers: Optional[Dict[str, str]]) -> Response:
        return self.request(url, "get", headers)

    def post(self, url: str, data: Optional[bytes], json: Optional[Dict], headers: Optional[Dict[str, str]]) -> Response:
        if json:
            data = json
        return self.request(url, "post", headers)
